import React, {useState} from 'react';
import './Search.css';
// import JSONDATA from ... DOES THIS NEED TO BE AN APISERVICE?

export default function Search() {
    const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
    }
  };

    const clearInput = () => {
        setFilteredData([]);
        setWordEntered("");
    };

    return (
        <div className="search">

            <form className="search-container"> 
                <h2>Search for a doctor</h2>

                {/* make this a pulldown menu? */}
                <div className="search-specialty"> 
                    <input type="text" placeholder="Search for a specialty or drug" 
                        value={wordEntered}
                        onClick={handleFilter}
                    />
                </div>

                <button type="submit" onClick={clearInput}>Search Doctors</button>
            </form>
        </div>
    )
}
