// https://github.com/machadop1407/React-Search-Bar/blob/main/search-bar-code/src/Components/SearchBar.js

export default function Search() {
    const [searchWord, setSearchWord] = useState('');

    const handleFilter = (event) => {
        const searchWord = event.target.value;
        setSearchWord(searchWord);
        const newFilter = JSONDATA.filter((value) => {
          return value.title.toLowerCase().includes(searchWord.toLowerCase());
        });

        if (searchWord === "") {
          setFilteredData([]);
        } else {
          setFilteredData(newFilter);
        }
      };

    return (
        <div className="search">

            <form className="search-container"> 
                <h2>Search for a doctor</h2>

                {/* make this a pulldown menu? */}
                <div className="search-specialty"> 
                    <input type="text" placeholder="Search for a specialty or drug" 
                        value={wordEntered}
                    />
                </div>

                <button type="submit" onClick={handleFilter}>Search Doctors</button>
            </form>
        </div>
    )
}
