import React, {useState, useEffect} from 'react';
import './DoctorList.css';
import Doctor from '../Doctor/Doctor';
import axios from 'axios';

export default function DoctorList(props) {
    const [allDoctors, setAllDoctors] = useState([]);
    const [searchTerm, setSearchTerm] = useState([]);

    useEffect(()=> {
            axios.get('https://ep-doctor-api.herokuapp.com/doctors')
            .then(response => {
            setAllDoctors(response.data)
        })
    },[]);

    function showDoctors(arr) {
        return arr.slice(Math.floor(Math.random()*20),Math.floor(Math.random() * 40)).map(doctor => {
            return (
                <Doctor key={doctor.id} doctor={doctor}/>
            )
        })
    }

    // function filterDoctors
    function filterDoctors(arr) {
        const input = searchTerm;
        return arr.filter(input).map(doctor => {
            //filter doctors based on input value
            return <Doctor key={doctor} doctor={doctor} />
        })
    }


    return(
        <div className="doctor-list" id="list">
            <div className="search-box"> 
                <input type="text" placeholder="Search for a drug" 
                onChange={(event) => setSearchTerm(event.target.value)}
                />
                <button>Search</button>
            </div>
            <div className="filtered-doctors"> 
                {seshowDoctors(allDoctors)}
            </div>
            {/* try a ternary like - "if searchWord exists
            then showDoctors 
            otherwise showFilteredDoctors" */}


            <div className="filtered-doctors">
            {/* {filteredData.length !== 0 && (
                <div className="dataResult">
                {filteredData.slice(0, 15).map((value, key) => {
                    return (
                    <a className="dataItem" href={value.link} >
                        <p>{value.title} </p>
                    </a>
                    );
                })}
                </div>
            )} */}
            </div>
        </div>
    )


}
