import React, {useState, useEffect} from 'react';
import './DoctorList.css';
import Doctor from '../Doctor/Doctor';
import axios from 'axios';

export default function DoctorList(props) {
    const [allDoctors, setAllDoctors] = useState([]);
    const [searchTerm, setSearchTerm] = useState(""); // full drug name 
    // const [filteredDoctors, setFilteredDoctors] = useState([]);

    useEffect(()=> {
            axios.get('https://ep-doctor-api.herokuapp.com/doctors')
            .then(response => {
            setAllDoctors(response.data)
        })
    },[]);

    function showDoctors(arr) {
        // const firstNum = Math.floor(Math.random()*20);
        // const lastNum = firstNum + Math.floor(Math.random()*20);
        return arr.slice(firstNum, lastNum).map(doctor => {
            return (
                <Doctor key={doctor.id} doctor={doctor}/>
            )
        })
    }

    // i'm not pulling in the input correctly
    function filterDoctors(arr, keyword) {
        const newArr = [...arr];
        const searchWord = keyword.toLowerCase();
        console.log("hi")
        console.log(newArr[9]);
        console.log(newArr[9].drug_list, "drug_list");
        console.log(keyword);
        //filter doctors based on input value
        for(let i=0;i<newArr.length; i++) {
            if(newArr[i].drug_list.includes(searchWord)) {
                return ( 
                        <Doctor key={newArr[i].id} doctor={newArr[i]} />
                    )
            }
        }
    }

    // const handleOnChange = async(e) => {
    //     let value = e.target.value;
    //     if (value.length > 4) {
    //         let search = await filterDoctors(filteredDoctors, value);
    //         setFilteredDoctors(search);
    //     } else {
    //         setFilteredDoctors(allDoctors);
    //     }
    // }

    return(
        <div className="doctor-list" id="list">
            <form onSubmit={ e =>
                {
                    e.preventDefault();
                    filterDoctors();
                }}>
                <div className="search-box"> 

                    <h2>Search For A Doctor By Drug Expertise:</h2>

                    <input type="text" placeholder="Search for a drug" 
                        value={searchTerm}
                        onChange={e => setSearchTerm(e.target.value)}
                    />
                    <button id="search-button">Search</button>
                </div>

                <div className="filtered-doctors-list"> 
                    {searchTerm.length ? filterDoctors(allDoctors, searchTerm) : showDoctors(allDoctors)}
                </div>
            </form>
        </div>
    )


}
